import { NextResponse } from "next/server";
import bcrypt from "bcryptjs";
import dbConnect from "@/lib/db";
import User from "@/models/User";

export async function POST(request: Request) {
    try {
        await dbConnect();
        const { email, password } = await request.json();

        const user = await User.findOne({ email });

        if (!user) {
            return NextResponse.json(
                { error: "Invalid credentials" },
                { status: 400 }
            );
        }

        const isMatch = await bcrypt.compare(password, user.password);

        if (!isMatch) {
            return NextResponse.json(
                { error: "Invalid credentials" },
                { status: 400 }
            );
        }

        // In a real app, you would set a session cookie or return a JWT here.
        // For this simple example, we just return success.
        return NextResponse.json({ success: true, data: { name: user.name, email: user.email } }, { status: 200 });
    } catch (error) {
        return NextResponse.json({ error: "Server error" }, { status: 500 });
    }
}

import { NextResponse } from "next/server";
import bcrypt from "bcryptjs";
import dbConnect from "@/lib/db";
import User from "@/models/User";

export async function POST(request: Request) {
    try {
        await dbConnect();
        const { name, email, password } = await request.json();

        const existingUser = await User.findOne({ email });

        if (existingUser) {
            return NextResponse.json(
                { error: "User already exists" },
                { status: 400 }
            );
        }

        const hashedPassword = await bcrypt.hash(password, 10);

        const user = await User.create({
            name,
            email,
            password: hashedPassword,
        });

        return NextResponse.json({ success: true, data: user }, { status: 201 });
    } catch (error) {
        return NextResponse.json({ error: "Server error" }, { status: 500 });
    }
}

import { NextResponse } from "next/server";
// import { getServerSession } from "next-auth";
import dbConnect from "@/lib/db";
import Expense from "@/models/Expense";

// Mock user for now
const userEmail = "demo@example.com";

export async function DELETE(
    req: Request,
    { params }: { params: { id: string } }
) {
    try {
        await dbConnect();
        const deleted = await Expense.findOneAndDelete({
            _id: params.id,
            userEmail,
        });

        if (!deleted) {
            return NextResponse.json({ error: "Expense not found" }, { status: 404 });
        }

        return NextResponse.json({ success: true });
    } catch (error) {
        return NextResponse.json({ error: "Error deleting expense" }, { status: 500 });
    }
}

export async function PUT(
    req: Request,
    { params }: { params: { id: string } }
) {
    try {
        await dbConnect();
        const data = await req.json();

        // Update the expense
        const updated = await Expense.findOneAndUpdate(
            { _id: params.id, userEmail },
            {
                amount: data.amount,
                category: data.category,
                description: data.description,
                timestamp: new Date() // Optionally update time, or keep original? user usually expects "edit" to reflected content, maybe not time. 
                // Let's NOT update timestamp to keep history accurate, or maybe update it? 
                // Usually editing a transaction fixes a mistake, so date might remain same. 
                // But for simplicity let's just update content fields.
            },
            { new: true }
        );

        if (!updated) {
            return NextResponse.json({ error: "Expense not found" }, { status: 404 });
        }

        return NextResponse.json(updated);
    } catch (error) {
        return NextResponse.json({ error: "Error updating expense" }, { status: 500 });
    }
}

