"use client";

import { useState, useEffect } from "react";
// import { useRouter } from "next/navigation"; // Unused
import { Check, Loader2, Sparkles, X, Target, CalendarDays, Sun, Moon, Briefcase } from "lucide-react";
import { clsx } from "clsx";

interface CustomTask {
    id: string;
    title: string;
    isCompleted: boolean;
    category: string;
    time?: string;
}

interface FocusData {
    customTasks?: CustomTask[];
    [key: string]: unknown; // Allow other fields
}

export function DailyFocus() {
    // const router = useRouter(); // Unused
    const [task, setTask] = useState<FocusData | null>(null);
    const [loading, setLoading] = useState(true);
    const [refreshKey, setRefreshKey] = useState(0);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const [modalCategory, setModalCategory] = useState<string>("All"); // All, Morning, Work, Night
    const [newTaskInput, setNewTaskInput] = useState("");

    const handleAddTask = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!newTaskInput.trim()) return;

        // Map UI category to DB category
        let dbCategory = modalCategory;
        if (modalCategory === "Work") dbCategory = "Goal";
        if (modalCategory === "All") dbCategory = "Morning";

        const newTask = {
            id: crypto.randomUUID(),
            title: newTaskInput.trim(),
            isCompleted: false,
            category: dbCategory,
            time: new Date().toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })
        };

        const updatedTasks = [...(task?.customTasks || []), newTask];
        const newTaskState = { ...task, customTasks: updatedTasks };

        // Optimistic update
        setTask(newTaskState);
        setNewTaskInput("");

        await updateTaskState({ customTasks: updatedTasks });
    }

    const userEmail = "adhisami2003@gmail.com";

    useEffect(() => {
        async function fetchTodayFocus() {
            try {
                const res = await fetch(`/api/focus/today?email=${userEmail}`);
                const data = await res.json();
                if (data.success && data.data) {
                    setTask(data.data);
                }
            } catch (err) {
                console.error(err);
            } finally {
                setLoading(false);
            }
        }
        fetchTodayFocus();
    }, [refreshKey, userEmail]);

    async function toggleCustomTask(id: string) {
        if (!task || !task.customTasks) return;
        const updatedCustomTasks = task.customTasks.map((t: CustomTask) =>
            t.id === id ? { ...t, isCompleted: !t.isCompleted } : t
        );
        const newTaskState = { ...task, customTasks: updatedCustomTasks };
        setTask(newTaskState);
        await updateTaskState({ customTasks: updatedCustomTasks });
    }

    async function updateTaskState(updates: Partial<FocusData>) {
        try {
            await fetch("/api/focus/today", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email: userEmail, ...updates }),
            });
            setRefreshKey(prev => prev + 1);
        } catch (err) { console.error(err); }
    }

    // Filter Tasks
    const allTasks = task?.customTasks || [];
    const morningTasks = allTasks.filter((t: CustomTask) => t.category === "Morning");
    const workTasks = allTasks.filter((t: CustomTask) => t.category === "Goal"); // 'Goal' map as Work
    const nightTasks = allTasks.filter((t: CustomTask) => t.category === "Night");

    // Helper to calc progress
    const getProgress = (tasks: CustomTask[]) => {
        const total = tasks.length;
        const completed = tasks.filter((t: CustomTask) => t.isCompleted).length;
        const pct = total === 0 ? 0 : Math.round((completed / total) * 100);
        return { total, completed, pct };
    };

    const morningStats = getProgress(morningTasks);
    const workStats = getProgress(workTasks);
    const nightStats = getProgress(nightTasks);

    // Filter for Modal
    const getModalTasks = () => {
        if (modalCategory === "Morning") return morningTasks;
        if (modalCategory === "Work") return workTasks;
        if (modalCategory === "Night") return nightTasks;
        return allTasks;
    };

    const modalTasks = getModalTasks();
    const modalStats = getProgress(modalTasks);

    if (loading && !task) {
        return (
            <div className="flex h-64 w-full items-center justify-center rounded-3xl bg-white/50 p-8 backdrop-blur-xl dark:bg-zinc-900/50">
                <Loader2 className="h-8 w-8 animate-spin text-purple-600" />
            </div>
        );
    }

    interface RoutineCardProps {
        title: string;
        stats: { total: number; completed: number; pct: number };
        icon: any;
        onClick: () => void;
        colorClass?: string;
        darkColorClass?: string; // unused but kept for prop structure if needed
        iconColor?: string;
    }

    // Reusable Card Component
    const RoutineCard = ({ title, stats, icon: Icon, onClick, colorClass, darkColorClass, iconColor }: RoutineCardProps) => {
        const isComplete = stats.total > 0 && stats.pct === 100;

        // Map titles to specific fill colors for the "water"
        const fillColors: Record<string, string> = {
            "Morning": "bg-orange-500/20 dark:bg-orange-500/30",
            "Work": "bg-blue-500/20 dark:bg-blue-500/30",
            "Night": "bg-indigo-500/20 dark:bg-indigo-500/30",
        };
        const fillColor = fillColors[title] || "bg-zinc-200/50";

        return (
            <button
                onClick={onClick}
                className={clsx(
                    "group relative flex flex-col items-center justify-center gap-3 overflow-hidden rounded-[1.5rem] p-2 aspect-square shadow-lg transition-all duration-500 hover:scale-[1.02] active:scale-95",
                    isComplete
                        ? "bg-emerald-50 ring-2 ring-emerald-500 dark:bg-emerald-900/10 dark:ring-emerald-500/50"
                        : "bg-white ring-1 ring-zinc-100 dark:bg-zinc-900 dark:ring-zinc-800"
                )}
            >
                {/* Water Fill Effect */}
                <div
                    className={clsx(
                        "absolute bottom-0 left-0 right-0 transition-all duration-1000 ease-in-out",
                        isComplete ? "bg-emerald-500/20" : fillColor
                    )}
                    style={{ height: `${stats.pct}%` }}
                />

                {/* Hover Gradient (Original) */}
                <div className={clsx(
                    "absolute inset-0 opacity-0 transition-opacity group-hover:opacity-100 bg-gradient-to-br",
                    isComplete ? "from-emerald-500/10 to-green-500/10 opacity-100" : colorClass
                )} />

                <div className={clsx(
                    "relative flex h-10 w-10 sm:h-14 sm:w-14 shrink-0 items-center justify-center rounded-full shadow-inner z-10 transition-colors",
                    isComplete ? "bg-emerald-100 dark:bg-emerald-900/30" : "bg-zinc-50 dark:bg-zinc-800"
                )}>
                    {isComplete ? (
                        <Check className="h-5 w-5 sm:h-7 sm:w-7 text-emerald-600 dark:text-emerald-400" strokeWidth={2.5} />
                    ) : (
                        <Icon className={clsx("h-5 w-5 sm:h-7 sm:w-7", iconColor)} strokeWidth={1.5} />
                    )}

                    {stats.total > 0 && !isComplete && (
                        <div className="absolute -right-1 -top-1 flex h-4 w-4 items-center justify-center rounded-full bg-green-500 text-[9px] font-bold text-white shadow-lg ring-2 ring-white dark:ring-zinc-900">
                            {stats.completed}/{stats.total}
                        </div>
                    )}
                </div>

                <div className="flex flex-col items-center relative z-10">
                    <h2 className={clsx("text-sm sm:text-base font-bold", isComplete ? "text-emerald-700 dark:text-emerald-300" : "text-zinc-900 dark:text-white")}>{title}</h2>
                    <div className="flex items-center gap-2">
                        <p className={clsx("text-[10px] sm:text-xs font-medium transition-colors",
                            isComplete ? "text-emerald-600/70 dark:text-emerald-400/70" : "text-zinc-400 group-hover:text-zinc-600 dark:group-hover:text-zinc-300"
                        )}>
                            {isComplete ? "Completed" : `${stats.pct}% Done`}
                        </p>
                    </div>
                </div>
            </button>
        );
    };

    return (
        <div className="flex flex-col items-center pb-20 w-full">

            {/* Header Date */}
            <div className="mt-4 mb-6 flex items-center gap-2 rounded-full bg-zinc-100 px-4 py-2 text-xs font-medium text-zinc-400 dark:bg-zinc-900">
                <CalendarDays className="h-3 w-3" />
                <span>{new Date().toLocaleDateString('en-US', { weekday: 'long', month: 'long', day: 'numeric' })}</span>
            </div>

            {/* Grid for Cards */}
            <div className="grid grid-cols-2 sm:grid-cols-3 gap-3 w-full max-w-4xl px-4">
                <RoutineCard
                    title="Morning"
                    stats={morningStats}
                    icon={Sun}
                    iconColor="text-orange-500"
                    colorClass="from-orange-500/10 to-yellow-500/10"
                    onClick={() => { setModalCategory("Morning"); setIsModalOpen(true); }}
                />
                <RoutineCard
                    title="Work"
                    stats={workStats}
                    icon={Briefcase}
                    iconColor="text-blue-500"
                    colorClass="from-blue-500/10 to-indigo-500/10"
                    onClick={() => { setModalCategory("Work"); setIsModalOpen(true); }}
                />
                <RoutineCard
                    title="Night"
                    stats={nightStats}
                    icon={Moon}
                    iconColor="text-indigo-500"
                    colorClass="from-indigo-500/10 to-purple-500/10"
                    onClick={() => { setModalCategory("Night"); setIsModalOpen(true); }}
                />
            </div>

            {/* ROUTINE POPUP MODAL */}
            {isModalOpen && (
                <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-md p-4 animate-in fade-in duration-300">
                    <div className="relative w-full max-w-lg overflow-hidden rounded-[2.5rem] bg-white shadow-2xl animate-in slide-in-from-bottom-10 zoom-in-95 duration-300 dark:bg-zinc-950">
                        {/* Header */}
                        <div className="relative flex items-center justify-between border-b border-zinc-100 p-6 sm:p-8 dark:border-zinc-800">
                            <div>
                                <h3 className="text-xl sm:text-2xl font-black text-zinc-900 dark:text-white">{modalCategory} Routine</h3>
                                <p className="text-xs sm:text-sm font-medium text-zinc-400">{modalStats.completed} of {modalStats.total} tasks completed</p>
                            </div>
                            <button
                                onClick={() => setIsModalOpen(false)}
                                className="rounded-full bg-zinc-100 p-2 sm:p-3 text-zinc-500 transition-colors hover:bg-zinc-200 dark:bg-zinc-900 dark:text-zinc-400 dark:hover:bg-zinc-800"
                            >
                                <X className="h-5 w-5 sm:h-6 sm:w-6" />
                            </button>
                        </div>

                        {/* List */}
                        <div className="max-h-[60vh] overflow-y-auto p-4 sm:p-6 scrollbar-hide">
                            <div className="space-y-3">
                                {modalTasks.length > 0 ? (
                                    modalTasks.map((t: CustomTask) => (
                                        <button
                                            key={t.id}
                                            onClick={() => toggleCustomTask(t.id)}
                                            className={clsx(
                                                "group flex w-full items-center gap-3 sm:gap-4 rounded-3xl border p-3 sm:p-4 text-left transition-all active:scale-[0.98]",
                                                t.isCompleted
                                                    ? "border-green-200 bg-green-50/50 dark:border-green-900/20 dark:bg-green-900/10"
                                                    : "border-zinc-100 bg-white hover:border-purple-200 hover:shadow-lg dark:border-zinc-800 dark:bg-zinc-900 dark:hover:border-purple-900/50"
                                            )}
                                        >
                                            <div className={clsx(
                                                "flex h-10 w-10 sm:h-12 sm:w-12 shrink-0 items-center justify-center rounded-2xl transition-colors",
                                                t.isCompleted
                                                    ? "bg-green-500 text-white"
                                                    : "bg-zinc-100 text-zinc-400 group-hover:bg-purple-100 group-hover:text-purple-600 dark:bg-zinc-800 dark:group-hover:bg-purple-900/30 dark:group-hover:text-purple-400"
                                            )}>
                                                {t.isCompleted ? <Check className="h-5 w-5 sm:h-6 sm:w-6" strokeWidth={3} /> : <Target className="h-5 w-5 sm:h-6 sm:w-6" />}
                                            </div>

                                            <div className="flex-1 min-w-0">
                                                <div className={clsx(
                                                    "text-base sm:text-lg font-bold truncate transition-all",
                                                    t.isCompleted ? "text-green-800 line-through opacity-50 dark:text-green-200" : "text-zinc-900 dark:text-white"
                                                )}>
                                                    {t.title}
                                                </div>
                                                <div className="flex items-center gap-2 mt-1">
                                                    {t.category && (
                                                        <span className="inline-flex items-center rounded-md bg-zinc-100 px-2 py-0.5 text-[10px] sm:text-xs font-semibold text-zinc-500 dark:bg-zinc-800">
                                                            {t.category}
                                                        </span>
                                                    )}
                                                </div>
                                            </div>
                                        </button>
                                    ))
                                ) : (
                                    <div className="flex min-h-[200px] flex-col items-center justify-center gap-4 rounded-3xl border border-dashed border-zinc-200 bg-zinc-50 dark:border-zinc-800 dark:bg-zinc-900/50">
                                        <div className="rounded-full bg-zinc-100 p-4 dark:bg-zinc-900">
                                            <Sparkles className="h-6 w-6 text-zinc-400" />
                                        </div>
                                        <p className="text-sm font-medium text-zinc-400">No {modalCategory} tasks set.</p>
                                    </div>
                                )}
                            </div>
                        </div>

                        {/* Footer - Inline Add */}
                        <div className="p-4 sm:p-5 border-t border-zinc-100 dark:border-zinc-800 bg-zinc-50/50 dark:bg-zinc-900/50 padding-bottom-safe">
                            <form onSubmit={handleAddTask} className="flex gap-2">
                                <input
                                    type="text"
                                    value={newTaskInput}
                                    onChange={(e) => setNewTaskInput(e.target.value)}
                                    placeholder={`Add a ${modalCategory} task...`}
                                    className="flex-1 rounded-xl bg-white px-4 py-3 text-sm font-medium text-zinc-900 placeholder:text-zinc-400 focus:outline-none focus:ring-2 focus:ring-purple-500/20 shadow-sm dark:bg-zinc-800 dark:text-white dark:focus:ring-purple-500/40"
                                />
                                <button
                                    type="submit"
                                    disabled={!newTaskInput.trim()}
                                    className="rounded-xl bg-zinc-900 px-4 py-2 font-bold text-white transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-black dark:hover:bg-zinc-200"
                                >
                                    Add
                                </button>
                            </form>
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

"use client";

import { useEffect, useState } from "react";
import { Loader2, TrendingUp, Flame, Calendar, Target } from "lucide-react";
import { WeeklyTracker } from "@/components/WeeklyTracker";

export function DashboardStats() {
    const [loading, setLoading] = useState(true);
    const [stats, setStats] = useState({ streak: 0, consistency: 0, completedCount: 0 });
    const userEmail = "adhisami2003@gmail.com";

    useEffect(() => {
        async function fetchStats() {
            try {
                const res = await fetch(`/api/focus/week?email=${userEmail}`);
                const data = await res.json();
                if (data.success) {
                    calculateStats(data.data);
                }
            } catch (err) {
                console.error("Failed to fetch stats", err);
            } finally {
                setLoading(false);
            }
        }
        fetchStats();
    }, [userEmail]);

    function calculateStats(last7Days: { date: string; isCompleted: boolean }[]) {
        // 1. Consistency: % of days with isCompleted
        const completed = last7Days.filter(d => d.isCompleted).length;
        const consistency = Math.round((completed / 7) * 100);

        // 2. Streak: Count backwards from today
        // Note: The API returns last 7 days. Ideally streak needs more history, 
        // but let's approximate with what we have or just count consecutive in the week window for now.
        // For a real app, we need a dedicated stats endpoint.
        let currentStreak = 0;
        // Sort by date descending (newest first)
        const sorted = [...last7Days].sort((a, b) => new Date(b.date).getTime() - new Date(a.date).getTime());

        for (const day of sorted) {
            if (day.isCompleted) {
                currentStreak++;
            } else {
                // Allow today to be incomplete if it's currently happening, but break if yesterday was missed
                const isToday = new Date(day.date).toDateString() === new Date().toDateString();
                if (!isToday) break;
            }
        }

        setStats({ streak: currentStreak, consistency, completedCount: completed });
    }

    if (loading) {
        return (
            <div className="flex justify-center p-8">
                <Loader2 className="h-6 w-6 animate-spin text-purple-600" />
            </div>
        );
    }

    return (
        <div className="space-y-6">
            {/* Header Greeting */}
            <div>
                <h1 className="text-2xl font-bold text-zinc-900 dark:text-white">
                    Hello, Adhithya 
                </h1>
                <p className="text-sm text-zinc-500 dark:text-zinc-400">
                    Here&apos;s your progress overview.
                </p>
            </div>

            {/* Stats Grid */}
            <div className="grid grid-cols-2 gap-4">
                {/* Streak Card */}
                <div className="rounded-2xl border border-orange-100 bg-orange-50/50 p-4 dark:border-orange-900/30 dark:bg-orange-900/10">
                    <div className="mb-2 flex h-8 w-8 items-center justify-center rounded-full bg-orange-100 text-orange-600 dark:bg-orange-900/30 dark:text-orange-400">
                        <Flame className="h-5 w-5" fill="currentColor" />
                    </div>
                    <div className="text-2xl font-bold text-zinc-900 dark:text-white">
                        {stats.streak} <span className="text-sm font-medium text-zinc-500">days</span>
                    </div>
                    <div className="text-xs font-medium text-orange-600 dark:text-orange-400">
                        Current Streak
                    </div>
                </div>

                {/* Consistency Card */}
                <div className="rounded-2xl border border-blue-100 bg-blue-50/50 p-4 dark:border-blue-900/30 dark:bg-blue-900/10">
                    <div className="mb-2 flex h-8 w-8 items-center justify-center rounded-full bg-blue-100 text-blue-600 dark:bg-blue-900/30 dark:text-blue-400">
                        <TrendingUp className="h-5 w-5" />
                    </div>
                    <div className="text-2xl font-bold text-zinc-900 dark:text-white">
                        {stats.consistency}%
                    </div>
                    <div className="text-xs font-medium text-blue-600 dark:text-blue-400">
                        Weekly Consistency
                    </div>
                </div>
            </div>

            {/* Weekly Tracker (Reuse existing component) */}
            <div className="rounded-2xl border border-zinc-200 bg-white p-5 shadow-sm dark:border-zinc-800 dark:bg-zinc-900">
                <div className="mb-4 flex items-center justify-between">
                    <h3 className="flex items-center gap-2 font-semibold text-zinc-900 dark:text-white">
                        <Calendar className="h-4 w-4 text-purple-500" />
                        Last 7 Days
                    </h3>
                    <span className="text-xs text-zinc-400">
                        {stats.completedCount}/7 Done
                    </span>
                </div>
                <WeeklyTracker refreshTrigger={0} />
            </div>

            {/* Motivation / Prompt */}
            <div className="rounded-2xl bg-gradient-to-r from-purple-600 to-indigo-600 p-6 text-white shadow-lg">
                <div className="mb-2 flex items-center gap-2 opacity-80">
                    <Target className="h-5 w-5" />
                    <span className="text-xs font-bold uppercase tracking-wider">Focus Mode</span>
                </div>
                <h3 className="mb-4 text-lg font-semibold leading-relaxed">
                    &quot;Success is the sum of small efforts, repeated day in and day out.&quot;
                </h3>
                {/* Visual indicator to click the center button */}
                <div className="flex items-center gap-2 text-sm text-purple-100 opacity-90">
                    <span>Tap the</span>
                    <div className="flex h-6 w-6 items-center justify-center rounded-full bg-white text-purple-600">
                        <div className="h-2 w-2 rounded-full bg-current" />
                    </div>
                    <span>button below to start today's task.</span>
                </div>
            </div>
        </div>
    );
}

"use client";

import { useState, useEffect, useCallback } from "react";
import { Coffee, Bus, ShoppingBag, Receipt, Film, MoreHorizontal, Plus, Wallet, Trash2, CalendarRange, TrendingUp, IndianRupee, ChevronDown, Tag, Pencil, Save, X } from "lucide-react";
import { clsx } from "clsx";

interface Expense {
    _id: string; // Changed to _id for Mongo
    description: string;
    amount: number;
    category: string;
    timestamp: string; // Date comes as string from JSON
}

interface Category {
    name: string;
    icon: any;
}

interface Stats {
    today: number;
    week: number;
    month: number;
}

export function ExpensesContent() {
    const [expenses, setExpenses] = useState<Expense[]>([]);
    const [description, setDescription] = useState("");
    const [amount, setAmount] = useState("");
    const [activeCategory, setActiveCategory] = useState("Food");
    const [isDropdownOpen, setIsDropdownOpen] = useState(false);
    const [isLoading, setIsLoading] = useState(true);
    const [stats, setStats] = useState<Stats>({ today: 0, week: 0, month: 0 });

    // Edit Mode State
    const [editingId, setEditingId] = useState<string | null>(null);

    // Category Management
    const [categories, setCategories] = useState<Category[]>([
        { name: "Food", icon: Coffee },
        { name: "Transport", icon: Bus },
        { name: "Shopping", icon: ShoppingBag },
        { name: "Bills", icon: Receipt },
        { name: "Fun", icon: Film },
        { name: "Other", icon: MoreHorizontal },
    ]);
    const [isAddingCategory, setIsAddingCategory] = useState(false);
    const [newCategoryName, setNewCategoryName] = useState("");

    const activeCatObj = categories.find(c => c.name === activeCategory) || categories[0];
    const ActiveIcon = activeCatObj.icon;

    const fetchData = useCallback(async () => {
        try {
            const [expRes, statsRes] = await Promise.all([
                fetch("/api/expenses"),
                fetch("/api/expenses/stats")
            ]);

            if (expRes.ok) {
                const data = await expRes.json();
                setExpenses(data);
            }
            if (statsRes.ok) {
                const data = await statsRes.json();
                setStats(data);
            }
        } catch (error) {
            console.error("Failed to fetch data", error);
        } finally {
            setIsLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const handleAddOrUpdateExpense = async (e: React.FormEvent) => {
        e.preventDefault();
        if (!amount) return;

        const val = parseFloat(amount);
        const payload = {
            description: description || activeCategory,
            amount: val,
            category: activeCategory,
        };

        try {
            let res;
            if (editingId) {
                // Update existing
                res = await fetch(`/api/expenses/${editingId}`, {
                    method: "PUT",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload),
                });
            } else {
                // Create new
                res = await fetch("/api/expenses", {
                    method: "POST",
                    headers: { "Content-Type": "application/json" },
                    body: JSON.stringify(payload),
                });
            }

            if (res.ok) {
                resetForm();
                fetchData(); // Refresh list and stats
            }
        } catch (error) {
            console.error("Failed to save", error);
        }
    };

    const resetForm = () => {
        setDescription("");
        setAmount("");
        setEditingId(null);
        setActiveCategory("Food");
        setIsDropdownOpen(false);
    };

    const handleEditClick = (expense: Expense) => {
        setEditingId(expense._id);
        setAmount(expense.amount.toString());
        setDescription(expense.description);
        setActiveCategory(expense.category);

        // Scroll to top on mobile
        window.scrollTo({ top: 0, behavior: 'smooth' });
    };

    const handleDelete = async (id: string) => {
        if (!confirm("Are you sure you want to delete this expense?")) return;
        try {
            const res = await fetch(`/api/expenses/${id}`, {
                method: "DELETE",
            });
            if (res.ok) {
                setExpenses(expenses.filter(e => e._id !== id));
                const statsRes = await fetch("/api/expenses/stats");
                if (statsRes.ok) setStats(await statsRes.json());

                // If editing deleted item, reset
                if (editingId === id) resetForm();
            }
        } catch (error) {
            console.error("Failed to delete", error);
        }
    };

    const handleCreateCategory = () => {
        if (!newCategoryName.trim()) return;

        const newCat: Category = {
            name: newCategoryName.trim(),
            icon: Tag
        };

        setCategories([...categories, newCat]);
        setActiveCategory(newCat.name);
        setNewCategoryName("");
        setIsAddingCategory(false);
        setIsDropdownOpen(false);
    };

    return (
        <div className="mx-auto max-w-md lg:max-w-6xl space-y-6 lg:space-y-8 pt-2 lg:pt-4 pb-20 lg:pb-0">

            {/* Stats Grid */}
            <div className="grid grid-cols-3 lg:grid-cols-3 gap-2 lg:gap-3 px-1 lg:px-0">
                <div className="flex flex-col items-center justify-center rounded-2xl bg-indigo-100/50 py-3 lg:py-6 ring-1 ring-inset ring-indigo-200 dark:bg-indigo-950/40 dark:ring-indigo-800/40 transition-all hover:scale-[1.02]">
                    <div className="flex items-center gap-1.5 mb-0.5 lg:mb-1">
                        <IndianRupee className="w-3 h-3 text-indigo-500 lg:hidden" />
                        <span className="text-[10px] lg:text-xs font-bold uppercase tracking-widest text-indigo-600 dark:text-indigo-300">Today</span>
                    </div>
                    <span className="text-sm lg:text-2xl font-bold text-indigo-950 dark:text-indigo-100">
                        {isLoading ? "..." : `${stats.today.toFixed(0)}`}
                    </span>
                </div>
                <div className="flex flex-col items-center justify-center rounded-2xl bg-fuchsia-100/50 py-3 lg:py-6 ring-1 ring-inset ring-fuchsia-200 dark:bg-fuchsia-950/40 dark:ring-fuchsia-800/40 transition-all hover:scale-[1.02]">
                    <div className="flex items-center gap-1.5 mb-0.5 lg:mb-1">
                        <TrendingUp className="w-3 h-3 text-fuchsia-500 lg:hidden" />
                        <span className="text-[10px] lg:text-xs font-bold uppercase tracking-widest text-fuchsia-600 dark:text-fuchsia-300">Week</span>
                    </div>
                    <span className="text-sm lg:text-2xl font-bold text-fuchsia-950 dark:text-fuchsia-100">
                        {isLoading ? "..." : `${stats.week.toFixed(0)}`}
                    </span>
                </div>
                <div className="flex flex-col items-center justify-center rounded-2xl bg-orange-100/50 py-3 lg:py-6 ring-1 ring-inset ring-orange-200 dark:bg-orange-950/40 dark:ring-orange-800/40 transition-all hover:scale-[1.02]">
                    <div className="flex items-center gap-1.5 mb-0.5 lg:mb-1">
                        <CalendarRange className="w-3 h-3 text-orange-500 lg:hidden" />
                        <span className="text-[10px] lg:text-xs font-bold uppercase tracking-widest text-orange-600 dark:text-orange-300">Month</span>
                    </div>
                    <span className="text-sm lg:text-2xl font-bold text-orange-950 dark:text-orange-100">
                        {isLoading ? "..." : `${stats.month.toFixed(0)}`}
                    </span>
                </div>
            </div>

            <div className="flex flex-col lg:flex-row gap-6 lg:gap-8">
                {/* Entry Card */}
                <div className="lg:w-1/2 lg:flex lg:flex-col lg:justify-start">
                    <div className={clsx(
                        "sticky top-6 relative overflow-visible rounded-3xl border bg-white p-4 lg:p-6 shadow-2xl transition-all duration-300 dark:bg-zinc-900 dark:shadow-black/60",
                        editingId ? "border-violet-500 shadow-violet-500/20 ring-2 ring-violet-500/20" : "border-zinc-100 dark:border-zinc-800 shadow-zinc-200/40"
                    )}>
                        <div className={clsx(
                            "absolute top-0 left-0 right-0 h-1.5 rounded-t-3xl transition-colors duration-300",
                            editingId ? "bg-violet-500" : "bg-gradient-to-r from-violet-500 via-fuchsia-500 to-orange-500 opacity-80"
                        )} />

                        {editingId && (
                            <div className="absolute top-4 right-4 animate-in fade-in slide-in-from-top-2">
                                <button
                                    onClick={resetForm}
                                    className="p-1.5 rounded-full bg-zinc-100 text-zinc-500 hover:bg-zinc-200 dark:bg-zinc-800 dark:text-zinc-400"
                                >
                                    <X className="h-4 w-4" />
                                </button>
                            </div>
                        )}

                        <form onSubmit={handleAddOrUpdateExpense} className="space-y-4 lg:space-y-6">
                            {/* Amount Input */}
                            <div className="flex flex-col items-center py-1">
                                {editingId && (
                                    <span className="text-xs font-bold text-violet-600 mb-1 uppercase tracking-widest">Editing Transaction</span>
                                )}
                                <div className="relative w-full">
                                    <div className="absolute top-1/2 left-1/2 -translate-x-[40px] lg:-translate-x-[50px] -translate-y-1/2 pointer-events-none">
                                        <span className="text-xl lg:text-2xl font-medium text-zinc-200 dark:text-zinc-700"></span>
                                    </div>
                                    <input
                                        type="number"
                                        inputMode="decimal"
                                        step="0.01"
                                        value={amount}
                                        onChange={(e) => setAmount(e.target.value)}
                                        placeholder="0"
                                        className="w-full bg-transparent text-center text-5xl lg:text-6xl font-black tracking-tight text-zinc-900 placeholder:text-zinc-100 focus:outline-none dark:text-white dark:placeholder:text-zinc-800"
                                        autoFocus
                                    />
                                </div>
                            </div>

                            {/* Category Dropdown */}
                            <div className="relative z-50">
                                <button
                                    type="button"
                                    onClick={() => {
                                        setIsDropdownOpen(!isDropdownOpen);
                                        setIsAddingCategory(false);
                                    }}
                                    className="w-full h-12 lg:h-14 flex items-center justify-between px-4 rounded-xl bg-zinc-50 border border-zinc-100 text-zinc-900 font-medium transition-all hover:bg-zinc-100 dark:bg-zinc-800/50 dark:border-zinc-700 dark:text-white dark:hover:bg-zinc-800"
                                >
                                    <div className="flex items-center gap-3">
                                        <div className="flex items-center justify-center p-1.5 rounded-lg bg-violet-100 text-violet-600 dark:bg-violet-900/40 dark:text-violet-300">
                                            <ActiveIcon className="h-4 w-4 lg:h-5 lg:w-5" />
                                        </div>
                                        <span>{activeCategory}</span>
                                    </div>
                                    <ChevronDown className={clsx("h-4 w-4 text-zinc-400 transition-transform", isDropdownOpen && "rotate-180")} />
                                </button>

                                {isDropdownOpen && (
                                    <div className="absolute top-full left-0 right-0 mt-2 p-2 rounded-2xl bg-white border border-zinc-100 shadow-xl dark:bg-zinc-900 dark:border-zinc-800 dark:shadow-black animate-in fade-in zoom-in-95 duration-200">
                                        {isAddingCategory ? (
                                            <div className="p-2 space-y-3">
                                                <input
                                                    type="text"
                                                    value={newCategoryName}
                                                    onChange={(e) => setNewCategoryName(e.target.value)}
                                                    placeholder="Category name..."
                                                    className="w-full rounded-lg bg-zinc-50 px-3 py-2 text-sm text-zinc-900 focus:outline-none focus:ring-2 focus:ring-violet-500 dark:bg-zinc-800 dark:text-white"
                                                    autoFocus
                                                />
                                                <div className="flex gap-2">
                                                    <button
                                                        type="button"
                                                        onClick={() => setIsAddingCategory(false)}
                                                        className="flex-1 px-3 py-2 rounded-lg bg-zinc-100 text-xs font-medium text-zinc-600 hover:bg-zinc-200 dark:bg-zinc-800 dark:text-zinc-400"
                                                    >
                                                        Cancel
                                                    </button>
                                                    <button
                                                        type="button"
                                                        onClick={handleCreateCategory}
                                                        className="flex-1 px-3 py-2 rounded-lg bg-violet-600 text-xs font-medium text-white hover:bg-violet-700"
                                                    >
                                                        Save
                                                    </button>
                                                </div>
                                            </div>
                                        ) : (
                                            <>
                                                <div className="max-h-56 overflow-y-auto space-y-0.5">
                                                    {categories.map((cat) => {
                                                        const Icon = cat.icon;
                                                        const isActive = activeCategory === cat.name;
                                                        return (
                                                            <button
                                                                key={cat.name}
                                                                type="button"
                                                                onClick={() => {
                                                                    setActiveCategory(cat.name);
                                                                    setIsDropdownOpen(false);
                                                                }}
                                                                className={clsx(
                                                                    "w-full flex items-center gap-3 px-3 py-2.5 rounded-xl transition-all text-sm font-medium",
                                                                    isActive
                                                                        ? "bg-violet-50 text-violet-900 dark:bg-violet-900/20 dark:text-violet-100"
                                                                        : "text-zinc-600 hover:bg-zinc-50 dark:text-zinc-400 dark:hover:bg-zinc-800"
                                                                )}
                                                            >
                                                                <Icon className={clsx("h-4 w-4", isActive ? "text-violet-600 dark:text-violet-400" : "text-zinc-400")} />
                                                                <span>{cat.name}</span>
                                                            </button>
                                                        );
                                                    })}
                                                </div>
                                                <div className="mt-1 pt-1 border-t border-zinc-100 dark:border-zinc-800">
                                                    <button
                                                        type="button"
                                                        onClick={() => setIsAddingCategory(true)}
                                                        className="w-full flex items-center gap-2 px-3 py-2 rounded-xl text-sm font-medium text-violet-600 hover:bg-violet-50 dark:text-violet-400 dark:hover:bg-violet-900/20"
                                                    >
                                                        <Plus className="h-4 w-4" />
                                                        Create New Category
                                                    </button>
                                                </div>
                                            </>
                                        )}
                                    </div>
                                )}
                            </div>

                            {/* Description & Add Button Row */}
                            <div className="flex gap-2 lg:gap-3">
                                <input
                                    type="text"
                                    value={description}
                                    onChange={(e) => setDescription(e.target.value)}
                                    placeholder="Add a note..."
                                    className="flex-1 rounded-xl bg-zinc-50 px-4 lg:px-5 py-3 lg:py-4 text-sm font-medium text-zinc-900 placeholder:text-zinc-400 focus:outline-none focus:ring-2 focus:ring-violet-100 focus:bg-white transition-all dark:bg-zinc-800/50 dark:text-white dark:focus:ring-violet-900/30 dark:focus:bg-zinc-800"
                                />
                                <button
                                    type="submit"
                                    disabled={!amount}
                                    className={clsx(
                                        "flex h-[46px] w-[46px] lg:h-[54px] lg:w-[54px] items-center justify-center rounded-xl text-white shadow-xl transition-all hover:scale-105 active:scale-95 disabled:opacity-50 disabled:shadow-none",
                                        editingId
                                            ? "bg-violet-600 shadow-violet-500/30"
                                            : "bg-zinc-900 shadow-zinc-900/20 dark:bg-white dark:text-black dark:shadow-white/10"
                                    )}
                                >
                                    {editingId ? <Save className="h-5 w-5 lg:h-6 lg:w-6" strokeWidth={2.5} /> : <Plus className="h-5 w-5 lg:h-6 lg:w-6" strokeWidth={2.5} />}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>

                {/* List - Right/Bottom */}
                <div className="lg:w-1/2 space-y-3 lg:space-y-4 px-2 lg:px-0">
                    <div className="flex items-center justify-between px-2">
                        <h3 className="text-sm font-semibold text-zinc-900 dark:text-white">Recent Transactions</h3>
                        <span className="text-xs text-zinc-500 cursor-pointer hover:text-violet-600">View All</span>
                    </div>

                    {isLoading && expenses.length === 0 ? (
                        <div className="flex justify-center py-8">
                            <div className="animate-pulse h-8 w-8 rounded-full bg-zinc-200 dark:bg-zinc-800"></div>
                        </div>
                    ) : expenses.length === 0 ? (
                        <div className="flex flex-col items-center justify-center py-10 lg:py-12 rounded-3xl border border-dashed border-zinc-200 dark:border-zinc-800 bg-zinc-50/50 dark:bg-zinc-900/50">
                            <Wallet className="h-10 w-10 text-zinc-300 mb-2" />
                            <p className="text-sm font-medium text-zinc-400">No activity today</p>
                        </div>
                    ) : (
                        expenses.map((expense) => {
                            const cat = categories.find(c => c.name === expense.category);
                            const Icon = cat ? cat.icon : Wallet;
                            const isEditing = editingId === expense._id;

                            return (
                                <div
                                    key={expense._id}
                                    className={clsx(
                                        "group flex items-center justify-between rounded-2xl bg-white p-4 lg:p-5 shadow-[0_2px_10px_-4px_rgba(0,0,0,0.05)] transition-all border dark:bg-zinc-900 dark:shadow-black/20",
                                        isEditing
                                            ? "border-violet-500 ring-1 ring-violet-500"
                                            : "border-zinc-100/50 dark:border-zinc-800 hover:shadow-md"
                                    )}
                                >
                                    <div className="flex items-center gap-3 lg:gap-4">
                                        <div className="flex h-10 w-10 lg:h-12 lg:w-12 items-center justify-center rounded-2xl bg-zinc-50 text-zinc-500 dark:bg-zinc-800 dark:text-zinc-400 group-hover:bg-violet-50 group-hover:text-violet-600 transition-colors">
                                            <Icon className="h-4 w-4 lg:h-5 lg:w-5" />
                                        </div>
                                        <div className="flex flex-col">
                                            <span className="text-sm font-bold text-zinc-900 dark:text-white group-hover:text-violet-700 transition-colors">
                                                {expense.description}
                                            </span>
                                            <span className="text-[10px] font-medium text-zinc-400">
                                                {new Date(expense.timestamp).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                            </span>
                                        </div>
                                    </div>
                                    <div className="flex items-center gap-2 lg:gap-3">
                                        <span className="font-bold text-base lg:text-lg text-zinc-900 dark:text-white mr-1">
                                            -{expense.amount.toFixed(2)}
                                        </span>

                                        <button
                                            onClick={() => handleEditClick(expense)}
                                            className="p-2 rounded-lg text-zinc-300 transition-all hover:bg-zinc-100 hover:text-violet-600 dark:hover:bg-zinc-800 opacity-0 group-hover:opacity-100"
                                            title="Edit"
                                        >
                                            <Pencil className="h-4 w-4" />
                                        </button>

                                        <button
                                            onClick={() => handleDelete(expense._id)}
                                            className="p-2 rounded-lg text-zinc-300 transition-all hover:bg-red-50 hover:text-red-500 dark:hover:bg-red-900/20 opacity-0 group-hover:opacity-100"
                                            title="Delete"
                                        >
                                            <Trash2 className="h-4 w-4" />
                                        </button>
                                    </div>
                                </div>
                            );
                        })
                    )}
                </div>
            </div>

        </div>
    );
}

