"use client";

import { useState, useRef, useEffect } from "react";
import { useRouter } from "next/navigation";
import { X, Loader2 } from "lucide-react";

export function PlansContent() {
    const [input, setInput] = useState("");
    const [goals, setGoals] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [saving, setSaving] = useState(false);
    const scrollRef = useRef<HTMLDivElement>(null);

    const router = useRouter();
    const userEmail = "adhisami2003@gmail.com";

    useEffect(() => {
        async function fetchPlan() {
            try {
                const res = await fetch(`/api/plans?email=${userEmail}`);
                const data = await res.json();
                if (data.success && data.data && data.data.goals) {
                    setGoals(data.data.goals);
                }
            } catch (err) {
                console.error(err);
            } finally {
                setLoading(false);
            }
        }
        fetchPlan();
    }, [userEmail]);

    // Scroll to bottom on added
    useEffect(() => {
        if (scrollRef.current) {
            scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
        }
    }, [goals]);

    const addGoal = () => {
        if (!input.trim()) return;

        const newGoal = {
            id: crypto.randomUUID(),
            title: input.trim(),
            isCompleted: false
        };

        setGoals([...goals, newGoal]);
        setInput("");
    };

    const removeGoal = (id: string) => {
        setGoals(goals.filter(g => g.id !== id));
    };

    async function handleSave() {
        setSaving(true);
        try {
            await fetch("/api/plans", {
                method: "PUT",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({
                    email: userEmail,
                    goals: goals
                }),
            });
            router.push("/dashboard/focus");
            router.refresh();
        } catch (err) { console.error(err); } finally {
            setSaving(false);
        }
    }

    if (loading) {
        return (
            <div className="flex h-screen w-full items-center justify-center bg-white dark:bg-zinc-950">
                <Loader2 className="h-8 w-8 animate-spin text-purple-600" />
            </div>
        );
    }

    return (
        <div className="flex min-h-screen flex-col bg-white transition-colors dark:bg-zinc-950">
            {/* Minimal Header */}
            <div className="mx-auto flex w-full max-w-2xl flex-col px-6 pb-24 pt-10">
                <div className="flex items-center justify-between">
                    <h1 className="text-3xl font-bold text-zinc-900 dark:text-white">Weekly Focus</h1>
                    <button onClick={handleSave} className="rounded-xl bg-zinc-900 px-4 py-2 font-bold text-white transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-black dark:hover:bg-zinc-200">
                        {saving ? "Saving..." : "Save"}
                    </button>
                </div>
                <p className="mt-2 text-sm text-zinc-500">Set your goals for the week.</p>

                <div className="mt-8 flex flex-col gap-4" ref={scrollRef}>
                    {goals.map((goal) => (
                        <div key={goal.id} className="flex items-center gap-4">
                            <input
                                type="checkbox"
                                checked={goal.isCompleted}
                                onChange={() => {
                                    const updatedGoals = goals.map(g => g.id === goal.id ? { ...g, isCompleted: !g.isCompleted } : g);
                                    setGoals(updatedGoals);
                                }}
                                className="h-5 w-5 rounded border-zinc-300 text-purple-600 focus:ring-purple-500"
                            />
                            <input
                                type="text"
                                value={goal.title}
                                onChange={(e) => {
                                    const updatedGoals = goals.map(g => g.id === goal.id ? { ...g, title: e.target.value } : g);
                                    setGoals(updatedGoals);
                                }}
                                className="flex-1 rounded-lg border-none bg-transparent text-lg font-medium text-zinc-700 focus:outline-none focus:ring-0 dark:text-white"
                            />
                            <button onClick={() => removeGoal(goal.id)} className="rounded-full p-2 text-zinc-400 transition-all hover:bg-zinc-100 hover:text-red-500 dark:hover:bg-zinc-800">
                                <X className="h-4 w-4" />
                            </button>
                        </div>
                    ))}
                </div>

                <div className="mt-8 flex items-center gap-4">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        placeholder="Add new goal..."
                        className="flex-1 rounded-lg border-none bg-transparent text-lg font-medium focus:outline-none focus:ring-0 dark:text-white"
                        onKeyDown={(e) => e.key === "Enter" && addGoal()}
                    />
                    <button onClick={addGoal} className="rounded-xl bg-zinc-900 px-4 py-2 font-bold text-white transition-all hover:bg-zinc-800 disabled:opacity-50 dark:bg-white dark:text-black dark:hover:bg-zinc-200">
                        Add
                    </button>
                </div>
            </div>
        </div>
    );
}

"use client";

import Link from "next/link";
import { usePathname } from "next/navigation";
import { LayoutDashboard, CheckSquare, Calendar, Settings, LogOut, X, CheckCircle2, User, Plus, Wallet, Target } from "lucide-react";
import { clsx } from "clsx";
import { twMerge } from "tailwind-merge";

const navItems = [
    { name: "Dashboard", href: "/dashboard", icon: LayoutDashboard },
    { name: "Weekly Plans", href: "/dashboard/plans", icon: Target },
    { name: "Task", href: "/dashboard/focus", icon: CheckCircle2 },
    { name: "Daily Expenses", href: "/dashboard/expenses", icon: Wallet },
    { name: "Profile", href: "/dashboard/profile", icon: User },
];

interface SidebarProps {
    isOpen: boolean;
    onClose: () => void;
}

export function Sidebar({ isOpen, onClose }: SidebarProps) {
    const pathname = usePathname();

    return (
        <>
            {/* Mobile Backdrop */}
            <div
                className={clsx(
                    "fixed inset-0 z-40 bg-black/50 backdrop-blur-sm lg:hidden",
                    isOpen ? "block" : "hidden"
                )}
                onClick={onClose}
            />

            {/* Sidebar Container */}
            <aside
                className={clsx(
                    "fixed inset-y-0 left-0 z-50 w-64 transform border-r border-zinc-200 bg-white shadow-xl transition-transform duration-300 dark:border-zinc-800 dark:bg-zinc-950 lg:static lg:translate-x-0",
                    isOpen ? "translate-x-0" : "-translate-x-full"
                )}
            >
                <div className="flex h-full flex-col">
                    {/* Logo Area */}
                    <div className="flex h-16 items-center justify-between px-6 border-b border-zinc-200 dark:border-zinc-800">
                        <Link href="/dashboard" className="flex items-center gap-2">
                            <div className="h-8 w-8 rounded-lg bg-purple-600 flex items-center justify-center text-white font-bold">
                                T
                            </div>
                            <span className="text-xl font-bold text-zinc-900 dark:text-white">
                                TaskTracker
                            </span>
                        </Link>
                        <button
                            onClick={onClose}
                            className="rounded-lg p-1 text-zinc-500 hover:bg-zinc-100 dark:text-zinc-400 dark:hover:bg-zinc-900 lg:hidden"
                        >
                            <X className="h-6 w-6" />
                        </button>
                    </div>

                    {/* Navigation Links */}
                    <nav className="flex-1 space-y-1 px-3 py-6">
                        {navItems.map((item) => {
                            const isActive = pathname === item.href;
                            const Icon = item.icon;

                            return (
                                <Link
                                    key={item.name}
                                    href={item.href}
                                    className={twMerge(
                                        "flex items-center gap-3 rounded-lg px-3 py-2.5 text-sm font-medium transition-all",
                                        isActive
                                            ? "bg-purple-50 text-purple-600 dark:bg-purple-500/10 dark:text-purple-400"
                                            : "text-zinc-600 hover:bg-zinc-50 hover:text-zinc-900 dark:text-zinc-400 dark:hover:bg-zinc-900 dark:hover:text-zinc-50"
                                    )}
                                >
                                    <Icon className="h-5 w-5" />
                                    {item.name}
                                </Link>
                            );
                        })}
                    </nav>

                    {/* User Profile / Logout */}
                    <div className="border-t border-zinc-200 p-4 dark:border-zinc-800">
                        <button
                            onClick={() => window.location.href = "/"}
                            className="flex w-full items-center gap-3 rounded-lg px-3 py-2.5 text-sm font-medium text-red-600 transition-all hover:bg-red-50 dark:text-red-400 dark:hover:bg-red-900/10"
                        >
                            <LogOut className="h-5 w-5" />
                            Sign Out
                        </button>
                    </div>
                </div>
            </aside>
        </>
    );
}

"use client";

import Link from "next/link";
import { useState } from "react";
import { useRouter } from "next/navigation";

export function SignupContent() {
    const [error, setError] = useState("");
    const [loading, setLoading] = useState(false);
    const router = useRouter();

    async function handleSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault();
        setLoading(true);
        setError("");

        const formData = new FormData(e.currentTarget);
        const name = formData.get("name");
        const email = formData.get("email");
        const password = formData.get("password");

        try {
            const res = await fetch("/api/auth/signup", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ name, email, password }),
            });

            const data = await res.json();

            if (!res.ok) {
                throw new Error(data.error || "Something went wrong");
            }

            // Successful signup, redirect to login (or dashboard)
            router.push("/");
        } catch (err: any) {
            setError(err.message);
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className="flex min-h-screen w-full items-center justify-center bg-zinc-50 dark:bg-black font-sans relative overflow-hidden">
            {/* Background Gradients */}
            <div className="absolute top-[-10%] right-[-10%] w-[40%] h-[40%] bg-purple-500/30 rounded-full blur-[120px] pointer-events-none mix-blend-screen animate-pulse" />
            <div className="absolute bottom-[-10%] left-[-10%] w-[40%] h-[40%] bg-blue-500/30 rounded-full blur-[120px] pointer-events-none mix-blend-screen animate-pulse delay-1000" />

            <div className="z-10 w-full max-w-md p-6">
                <div className="rounded-2xl border border-zinc-200 bg-white/50 p-8 shadow-xl backdrop-blur-xl dark:border-zinc-800 dark:bg-zinc-900/50">
                    <div className="mb-8 flex flex-col items-center text-center">
                        <h1 className="mb-2 text-3xl font-bold tracking-tight text-zinc-900 dark:text-zinc-50">
                            Create an account
                        </h1>
                        <p className="text-sm text-zinc-500 dark:text-zinc-400">
                            Enter your details to get started
                        </p>
                    </div>

                    <form className="space-y-4" onSubmit={handleSubmit}>
                        {error && (
                            <div className="text-red-500 text-sm text-center">{error}</div>
                        )}
                        <div>
                            <label
                                htmlFor="name"
                                className="mb-1.5 block text-sm font-medium text-zinc-700 dark:text-zinc-300"
                            >
                                Full Name
                            </label>
                            <input
                                id="name"
                                name="name"
                                type="text"
                                placeholder="John Doe"
                                required
                                className="w-full rounded-xl border border-zinc-200 bg-white px-4 py-2.5 text-sm outline-none transition-all placeholder:text-zinc-400 focus:border-purple-500 focus:ring-4 focus:ring-purple-500/10 dark:border-zinc-800 dark:bg-zinc-950 dark:focus:border-purple-500"
                            />
                        </div>
                        <div>
                            <label
                                htmlFor="email"
                                className="mb-1.5 block text-sm font-medium text-zinc-700 dark:text-zinc-300"
                            >
                                Email
                            </label>
                            <input
                                id="email"
                                name="email"
                                type="email"
                                placeholder="name@example.com"
                                required
                                className="w-full rounded-xl border border-zinc-200 bg-white px-4 py-2.5 text-sm outline-none transition-all placeholder:text-zinc-400 focus:border-purple-500 focus:ring-4 focus:ring-purple-500/10 dark:border-zinc-800 dark:bg-zinc-950 dark:focus:border-purple-500"
                            />
                        </div>
                        <div>
                            <label
                                htmlFor="password"
                                className="mb-1.5 block text-sm font-medium text-zinc-700 dark:text-zinc-300"
                            >
                                Password
                            </label>
                            <input
                                id="password"
                                name="password"
                                type="password"
                                required
                                className="w-full rounded-xl border border-zinc-200 bg-white px-4 py-2.5 text-sm outline-none transition-all placeholder:text-zinc-400 focus:border-purple-500 focus:ring-4 focus:ring-purple-500/10 dark:border-zinc-800 dark:bg-zinc-950 dark:focus:border-purple-500"
                            />
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="mt-2 w-full rounded-xl bg-purple-600 px-4 py-2.5 text-sm font-semibold text-white shadow-md transition-all hover:bg-purple-500 hover:shadow-lg focus:outline-none focus:ring-4 focus:ring-purple-600/20 active:scale-[0.98] disabled:opacity-50"
                        >
                            {loading ? "Creating account..." : "Sign Up"}
                        </button>
                    </form>

                    <p className="mt-8 text-center text-sm text-zinc-500 dark:text-zinc-400">
                        Already have an account?{" "}
                        <Link
                            href="/"
                            className="font-semibold text-purple-600 hover:text-purple-500 dark:text-purple-400"
                        >
                            Sign in
                        </Link>
                    </p>
                </div>
            </div>
        </div>
    );
}
